mapper.select:

SELECT AM.*,
M.nickname AS extra__writer
FROM applyment AS AM
INNER JOIN member AS M
ON AM.memberId = M.id
WHERE AM.relId = #{relId}
AND AM.relTypeCode = #{relTypeCode}
AND AM.displayStatus = 1
AND AM.id >= #{from}
ORDER BY AM.id ASC

mapper.update:
UPDATE applyment
updateDate = NOW(), body = #{body},
WHERE id = #{id}
