mapper.select:

SELECT *
FROM attr
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND (expireDate >= NOW() OR expireDate IS NULL)


SELECT value
FROM attr
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND (expireDate >= NOW() OR expireDate IS NULL)

mapper.delete:
DELETE FROM attr
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}

mapper.update:
UPDATE article
updateDate = NOW(), title = #{title}, body = #{body},
WHERE id = #{id}
