mapper.update:
UPDATE `file`
updateDate = NOW(), originFileName = #{originFileName}, fileDir = #{fileDir}, fileExt = #{fileExt}, fileSize = #{fileSize}, fileExtTypeCode = #{fileExtTypeCode}, fileExtType2Code = #{fileExtType2Code},
WHERE id = #{id}

mapper.select:
SELECT *
FROM file
WHERE 1
AND relId IN

	#{relId}

AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND fileNo = #{fileNo}
ORDER BY relId

SELECT *
FROM file
WHERE 1
AND relId IN

	#{relId}

AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
ORDER BY relId, fileNo

SELECT *
FROM file
WHERE 1
AND relId = #{relId}
AND relTypeCode
= #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
ORDER BY fileNo

mapper.update:
UPDATE `file`

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.delete:
DELETE FROM file
WHERE relId = #{relId}
AND relTypeCode
= #{relTypeCode}

mapper.select:
SELECT id
FROM file
WHERE
relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode =
#{typeCode}
AND type2Code = #{type2Code}
AND fileNo = #{fileNo}
